package com.belief.test;

import java.io.File;
import java.io.FileInputStream;
import java.io.FileOutputStream;
import java.io.IOException;
import java.io.InputStream;
import java.io.OutputStream;

import org.junit.Ignore;
import org.junit.Test;
import org.springframework.util.Base64Utils;

public class ImageBase64Test {

	@Ignore
	@Test
	public void testImg() {
		System.out.println(GetImageStr());
	}

	@Test
	public void test() throws Exception {
		String path = "D:\\";
		// String data =
		// "";
		String data = "";
		File imageFile = new File(path, System.currentTimeMillis() + "11111.jpeg");
		// Base64解码
		byte[] bytes = Base64Utils.decodeFromString(data);
		for (int i = 0; i < bytes.length; ++i) {
			if (bytes[i] < 0) {// 调整异常数据
				bytes[i] += 256;
			}
		}
		// 生成jpeg图片
		OutputStream out = new FileOutputStream(imageFile);
		out.write(bytes);
		out.flush();
		out.close();
	}

	public static String GetImageStr() {
		String imgFile = "d://C7CA0DQG8Y9O.jpg";
		InputStream in = null;
		byte[] data = null;
		// 读取图片字节数组
		try {
			in = new FileInputStream(imgFile);
			data = new byte[in.available()];
			in.read(data);
			in.close();
		} catch (IOException e) {
			e.printStackTrace();
		}
		return Base64Utils.encodeToString(data);
	}
}
